import React, { useState, useEffect } from 'react';
import { 
    Box, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, 
    TableRow, Button, IconButton, Dialog, DialogActions, DialogContent, DialogTitle, 
    TextField, MenuItem, Select, FormControl, InputLabel, Chip, Tooltip, Grid, 
    Divider, List, ListItem, ListItemText, Tabs, Tab, FormHelperText
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Layout from '../components/Layout';
import { getOrders, getOrder, createOrder, updateOrder, deleteOrder, getProducts, getUsers } from '../services/api';
import { Order, Product, User, OrderDetail } from '../types';
import { useSnackbar } from 'notistack';

interface TabPanelProps {
    children?: React.ReactNode;
    index: number;
    value: number;
}

function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`order-tabpanel-${index}`}
            aria-labelledby={`order-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 2 }}>
                    {children}
                </Box>
            )}
        </div>
    );
}

// Định dạng số tiền
const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(amount);
};

// Định dạng ngày giờ
const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('vi-VN');
};

// Lấy màu cho trạng thái đơn hàng
const getStatusColor = (status: string) => {
    switch (status) {
        case 'Pending':
            return 'warning';
        case 'Processing':
            return 'info';
        case 'In transit':
            return 'primary';
        case 'Completed':
            return 'success';
        default:
            return 'default';
    }
};

const OrderManagement: React.FC = () => {
    const [orders, setOrders] = useState<Order[]>([]);
    const [products, setProducts] = useState<Product[]>([]);
    const [users, setUsers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [openDialog, setOpenDialog] = useState(false);
    const [editingOrder, setEditingOrder] = useState<Order | null>(null);
    const [tabValue, setTabValue] = useState(0);
    const [orderDetails, setOrderDetails] = useState<OrderDetail[]>([]);
    
    // Form data cho đơn hàng
    const [formData, setFormData] = useState({
        user: '',
        customer_name: '',
        customer_email: '',
        customer_phone: '',
        shipping_address: '',
        total_amount: 0,
        order_status: 'Pending'
    });
    
    // Form data cho chi tiết đơn hàng đang thêm
    const [detailForm, setDetailForm] = useState({
        product: '',
        quantity: 1,
        price: 0
    });
    
    const { enqueueSnackbar } = useSnackbar();

    useEffect(() => {
        fetchOrders();
        fetchProducts();
        fetchUsers();
    }, []);

    const fetchOrders = async () => {
        try {
            setLoading(true);
            const response = await getOrders();
            setOrders(response.data);
        } catch (error) {
            console.error('Không thể lấy dữ liệu đơn hàng:', error);
            enqueueSnackbar('Không thể lấy danh sách đơn hàng', { variant: 'error' });
        } finally {
            setLoading(false);
        }
    };
    
    const fetchProducts = async () => {
        try {
            const response = await getProducts();
            setProducts(response.data);
        } catch (error) {
            console.error('Không thể lấy dữ liệu sản phẩm:', error);
        }
    };
    
    const fetchUsers = async () => {
        try {
            const response = await getUsers();
            setUsers(response.data);
        } catch (error) {
            console.error('Không thể lấy dữ liệu người dùng:', error);
        }
    };

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
        setTabValue(newValue);
    };

    const handleOpenDialog = (order?: Order) => {
        if (order) {
            setEditingOrder(order);
            setFormData({
                user: order.user ? String(order.user) : '',
                customer_name: order.customer_name || '',
                customer_email: order.customer_email || '',
                customer_phone: order.customer_phone || '',
                shipping_address: order.shipping_address || '',
                total_amount: order.total_amount,
                order_status: order.order_status
            });
            setOrderDetails([...order.details]);
        } else {
            setEditingOrder(null);
            setFormData({
                user: '',
                customer_name: '',
                customer_email: '',
                customer_phone: '',
                shipping_address: '',
                total_amount: 0,
                order_status: 'Pending'
            });
            setOrderDetails([]);
        }
        setOpenDialog(true);
        setTabValue(0);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setEditingOrder(null);
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
        const { name, value } = e.target;
        if (name) {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };
    
    const handleDetailInputChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
        const { name, value } = e.target;
        if (name) {
            setDetailForm(prev => ({
                ...prev,
                [name]: value
            }));
            
            // Tự động cập nhật giá khi chọn sản phẩm
            if (name === 'product') {
                const selectedProduct = products.find(p => p.product_id === Number(value));
                if (selectedProduct) {
                    setDetailForm(prev => ({
                        ...prev,
                        price: selectedProduct.price
                    }));
                }
            }
        }
    };
    
    const handleAddDetail = () => {
        if (!detailForm.product) {
            enqueueSnackbar('Vui lòng chọn sản phẩm', { variant: 'error' });
            return;
        }
        
        if (detailForm.quantity <= 0) {
            enqueueSnackbar('Số lượng phải lớn hơn 0', { variant: 'error' });
            return;
        }
        
        const selectedProduct = products.find(p => p.product_id === Number(detailForm.product));
        if (!selectedProduct) return;
        
        const newDetail: OrderDetail = {
            order_detail_id: Math.random(), // Temporary ID for UI
            product: selectedProduct.product_id,
            product_name: selectedProduct.name,
            quantity: Number(detailForm.quantity),
            price: Number(detailForm.price)
        };
        
        setOrderDetails(prev => [...prev, newDetail]);
        
        // Cập nhật tổng tiền
        const newTotal = orderDetails.reduce((sum, detail) => 
            sum + (detail.quantity * detail.price), 0) + (newDetail.quantity * newDetail.price);
        
        setFormData(prev => ({
            ...prev,
            total_amount: newTotal
        }));
        
        // Reset form
        setDetailForm({
            product: '',
            quantity: 1,
            price: 0
        });
    };
    
    const handleRemoveDetail = (detailId: number) => {
        const detailToRemove = orderDetails.find(d => d.order_detail_id === detailId);
        if (!detailToRemove) return;
        
        setOrderDetails(prev => prev.filter(d => d.order_detail_id !== detailId));
        
        // Cập nhật tổng tiền
        const newTotal = orderDetails
            .filter(d => d.order_detail_id !== detailId)
            .reduce((sum, detail) => sum + (detail.quantity * detail.price), 0);
        
        setFormData(prev => ({
            ...prev,
            total_amount: newTotal
        }));
    };

    const handleSubmit = async () => {
        // Kiểm tra dữ liệu
        if (orderDetails.length === 0) {
            enqueueSnackbar('Đơn hàng phải có ít nhất một sản phẩm', { variant: 'error' });
            return;
        }
        
        // Nếu không chọn user thì phải có thông tin khách hàng
        if (!formData.user && !formData.customer_name) {
            enqueueSnackbar('Vui lòng chọn khách hàng hoặc nhập thông tin khách hàng', { variant: 'error' });
            return;
        }
        
        try {
            const orderData: any = {
                user: formData.user ? Number(formData.user) : null,
                customer_name: formData.customer_name || null,
                customer_email: formData.customer_email || null,
                customer_phone: formData.customer_phone || null,
                shipping_address: formData.shipping_address || null,
                total_amount: formData.total_amount,
                order_status: formData.order_status,
                details: orderDetails.map(detail => ({
                    product: detail.product,
                    quantity: detail.quantity,
                    price: detail.price
                }))
            };
            
            if (editingOrder) {
                // Cập nhật đơn hàng
                await updateOrder(editingOrder.order_id, orderData);
                enqueueSnackbar('Cập nhật đơn hàng thành công!', { variant: 'success' });
            } else {
                // Tạo đơn hàng mới
                await createOrder(orderData);
                enqueueSnackbar('Tạo đơn hàng mới thành công!', { variant: 'success' });
            }
            
            handleCloseDialog();
            fetchOrders();
        } catch (error: any) {
            console.error('Lỗi khi lưu đơn hàng:', error);
            if (error.response) {
                console.error('Lỗi response:', error.response.data);
                enqueueSnackbar(`Lỗi: ${JSON.stringify(error.response.data)}`, { variant: 'error' });
            } else {
                enqueueSnackbar('Lỗi khi lưu dữ liệu đơn hàng', { variant: 'error' });
            }
        }
    };

    const handleDelete = async (id: number) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa đơn hàng này?')) {
            try {
                await deleteOrder(id);
                enqueueSnackbar('Xóa đơn hàng thành công!', { variant: 'success' });
                fetchOrders();
            } catch (error) {
                console.error('Lỗi khi xóa đơn hàng:', error);
                enqueueSnackbar('Không thể xóa đơn hàng', { variant: 'error' });
            }
        }
    };

    return (
        <Layout>
            <Box sx={{ mb: 4 }}>
                <Typography variant="h4" gutterBottom>
                    Quản lý đơn hàng
                </Typography>
                <Button 
                    variant="contained" 
                    startIcon={<AddIcon />} 
                    onClick={() => handleOpenDialog()}
                    sx={{ mb: 2 }}
                >
                    Tạo đơn hàng mới
                </Button>
                
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>ID</TableCell>
                                <TableCell>Khách hàng</TableCell>
                                <TableCell>Tổng tiền</TableCell>
                                <TableCell>Trạng thái</TableCell>
                                <TableCell>Ngày tạo</TableCell>
                                <TableCell>Sản phẩm</TableCell>
                                <TableCell align="right">Thao tác</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {loading ? (
                                <TableRow>
                                    <TableCell colSpan={7} align="center">Đang tải...</TableCell>
                                </TableRow>
                            ) : orders.length === 0 ? (
                                <TableRow>
                                    <TableCell colSpan={7} align="center">Không có dữ liệu</TableCell>
                                </TableRow>
                            ) : (
                                orders.map((order) => (
                                    <TableRow key={order.order_id}>
                                        <TableCell>{order.order_id}</TableCell>
                                        <TableCell>
                                            {order.username || order.customer_name || 'Khách vãng lai'}
                                            {order.customer_email && (
                                                <Typography variant="body2" color="textSecondary">
                                                    {order.customer_email}
                                                </Typography>
                                            )}
                                        </TableCell>
                                        <TableCell>{formatCurrency(order.total_amount)}</TableCell>
                                        <TableCell>
                                            <Chip 
                                                label={order.order_status} 
                                                color={getStatusColor(order.order_status) as any}
                                                size="small"
                                            />
                                        </TableCell>
                                        <TableCell>{formatDate(order.created_at)}</TableCell>
                                        <TableCell>
                                            {order.details.length} sản phẩm
                                        </TableCell>
                                        <TableCell align="right">
                                            <IconButton onClick={() => handleOpenDialog(order)}>
                                                <EditIcon />
                                            </IconButton>
                                            <IconButton onClick={() => handleDelete(order.order_id)}>
                                                <DeleteIcon />
                                            </IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>

                {/* Dialog Thêm/Sửa Đơn hàng */}
                <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                    <DialogTitle>
                        {editingOrder ? 'Sửa đơn hàng' : 'Tạo đơn hàng mới'}
                    </DialogTitle>
                    <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                        <Tabs value={tabValue} onChange={handleTabChange}>
                            <Tab label="Thông tin đơn hàng" />
                            <Tab label="Chi tiết sản phẩm" />
                        </Tabs>
                    </Box>
                    <DialogContent>
                        <TabPanel value={tabValue} index={0}>
                            <Grid container spacing={2}>
                                <Grid item xs={12}>
                                    <Typography variant="subtitle2" gutterBottom>
                                        Loại đơn hàng
                                    </Typography>
                                    <Divider sx={{ mb: 2 }} />
                                </Grid>
                                
                                <Grid item xs={12} md={6}>
                                    <FormControl fullWidth>
                                        <InputLabel id="user-label">Khách hàng có tài khoản</InputLabel>
                                        <Select
                                            labelId="user-label"
                                            name="user"
                                            value={formData.user}
                                            label="Khách hàng có tài khoản"
                                            onChange={handleInputChange}
                                        >
                                            <MenuItem value="">
                                                <em>Không chọn (Khách không có tài khoản)</em>
                                            </MenuItem>
                                            {users.map(user => (
                                                <MenuItem key={user.user_id} value={user.user_id}>
                                                    {user.username} - {user.email}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                        <FormHelperText>
                                            Nếu chọn khách hàng có tài khoản, các thông tin bên dưới sẽ được bỏ qua
                                        </FormHelperText>
                                    </FormControl>
                                </Grid>
                                
                                <Grid item xs={12}>
                                    <Typography variant="subtitle2" gutterBottom>
                                        Thông tin khách hàng không có tài khoản
                                    </Typography>
                                    <Divider sx={{ mb: 2 }} />
                                </Grid>
                                
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Tên khách hàng"
                                        name="customer_name"
                                        value={formData.customer_name}
                                        onChange={handleInputChange}
                                        disabled={!!formData.user}
                                    />
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Email"
                                        name="customer_email"
                                        value={formData.customer_email}
                                        onChange={handleInputChange}
                                        disabled={!!formData.user}
                                    />
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Số điện thoại"
                                        name="customer_phone"
                                        value={formData.customer_phone}
                                        onChange={handleInputChange}
                                        disabled={!!formData.user}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Địa chỉ giao hàng"
                                        name="shipping_address"
                                        value={formData.shipping_address}
                                        onChange={handleInputChange}
                                        multiline
                                        rows={2}
                                        disabled={!!formData.user}
                                    />
                                </Grid>
                                
                                <Grid item xs={12}>
                                    <Typography variant="subtitle2" gutterBottom>
                                        Thông tin đơn hàng
                                    </Typography>
                                    <Divider sx={{ mb: 2 }} />
                                </Grid>
                                
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Tổng tiền"
                                        name="total_amount"
                                        value={formData.total_amount}
                                        onChange={handleInputChange}
                                        type="number"
                                        InputProps={{
                                            readOnly: true
                                        }}
                                    />
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <FormControl fullWidth>
                                        <InputLabel id="status-label">Trạng thái</InputLabel>
                                        <Select
                                            labelId="status-label"
                                            name="order_status"
                                            value={formData.order_status}
                                            label="Trạng thái"
                                            onChange={handleInputChange}
                                        >
                                            <MenuItem value="Pending">Chờ xử lý</MenuItem>
                                            <MenuItem value="Processing">Đang xử lý</MenuItem>
                                            <MenuItem value="In transit">Đang giao hàng</MenuItem>
                                            <MenuItem value="Completed">Hoàn thành</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                            </Grid>
                        </TabPanel>
                        
                        <TabPanel value={tabValue} index={1}>
                            <Box sx={{ mb: 3 }}>
                                <Typography variant="subtitle1" gutterBottom>
                                    Thêm sản phẩm vào đơn hàng
                                </Typography>
                                <Grid container spacing={2} alignItems="center">
                                    <Grid item xs={12} md={4}>
                                        <FormControl fullWidth>
                                            <InputLabel id="product-label">Sản phẩm</InputLabel>
                                            <Select
                                                labelId="product-label"
                                                name="product"
                                                value={detailForm.product}
                                                label="Sản phẩm"
                                                onChange={handleDetailInputChange}
                                            >
                                                <MenuItem value="">
                                                    <em>Chọn sản phẩm</em>
                                                </MenuItem>
                                                {products.map(product => (
                                                    <MenuItem key={product.product_id} value={product.product_id}>
                                                        {product.name} - {formatCurrency(product.price)}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} md={2}>
                                        <TextField
                                            fullWidth
                                            label="Số lượng"
                                            name="quantity"
                                            value={detailForm.quantity}
                                            onChange={handleDetailInputChange}
                                            type="number"
                                            inputProps={{ min: 1 }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={3}>
                                        <TextField
                                            fullWidth
                                            label="Đơn giá"
                                            name="price"
                                            value={detailForm.price}
                                            onChange={handleDetailInputChange}
                                            type="number"
                                            inputProps={{ min: 0 }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={3}>
                                        <Button 
                                            variant="contained" 
                                            onClick={handleAddDetail}
                                            fullWidth
                                        >
                                            Thêm vào đơn hàng
                                        </Button>
                                    </Grid>
                                </Grid>
                            </Box>
                            
                            <Divider sx={{ my: 2 }} />
                            
                            <Typography variant="subtitle1" gutterBottom>
                                Sản phẩm trong đơn hàng
                            </Typography>
                            
                            {orderDetails.length === 0 ? (
                                <Box sx={{ textAlign: 'center', py: 2 }}>
                                    <ShoppingCartIcon sx={{ fontSize: 40, color: 'text.secondary', mb: 1 }} />
                                    <Typography color="text.secondary">
                                        Chưa có sản phẩm nào trong đơn hàng
                                    </Typography>
                                </Box>
                            ) : (
                                <List>
                                    {orderDetails.map((detail, index) => {
                                        const product = products.find(p => p.product_id === detail.product);
                                        
                                        return (
                                            <React.Fragment key={detail.order_detail_id}>
                                                <ListItem
                                                    secondaryAction={
                                                        <IconButton 
                                                            edge="end" 
                                                            onClick={() => handleRemoveDetail(detail.order_detail_id)}
                                                        >
                                                            <DeleteIcon />
                                                        </IconButton>
                                                    }
                                                >
                                                    <ListItemText
                                                        primary={
                                                            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                                                                <Typography>
                                                                    {product?.name || detail.product_name}
                                                                </Typography>
                                                                <Typography>
                                                                    {formatCurrency(detail.price * detail.quantity)}
                                                                </Typography>
                                                            </Box>
                                                        }
                                                        secondary={
                                                            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                                                                <Typography variant="body2">
                                                                    Số lượng: {detail.quantity}
                                                                </Typography>
                                                                <Typography variant="body2">
                                                                    Đơn giá: {formatCurrency(detail.price)}
                                                                </Typography>
                                                            </Box>
                                                        }
                                                    />
                                                </ListItem>
                                                {index < orderDetails.length - 1 && <Divider />}
                                            </React.Fragment>
                                        );
                                    })}
                                    
                                    <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2, p: 2, bgcolor: 'grey.100' }}>
                                        <Typography variant="h6">
                                            Tổng tiền: {formatCurrency(formData.total_amount)}
                                        </Typography>
                                    </Box>
                                </List>
                            )}
                        </TabPanel>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleCloseDialog}>Hủy</Button>
                        <Button 
                            onClick={handleSubmit} 
                            variant="contained"
                            disabled={orderDetails.length === 0}
                        >
                            Lưu
                        </Button>
                    </DialogActions>
                </Dialog>
            </Box>
        </Layout>
    );
};

export default OrderManagement;
