import React, { useState, useEffect } from 'react';
import {
    Box, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, 
    TableRow, Chip, CircularProgress, Alert
} from '@mui/material';
import axios from 'axios';

// Định nghĩa kiểu dữ liệu cho người trong hàng đợi
interface QueueItem {
    id: number;
    name: string;
    phone: string;
    joined_at: string;
    status: 'waiting' | 'served' | 'cancelled';
}

const QueueView: React.FC = () => {
    const [queue, setQueue] = useState<QueueItem[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    
    // WebSocket connection for real-time updates
    const [socket, setSocket] = useState<WebSocket | null>(null);
    
    // Format date for display
    const formatTime = (dateString: string) => {
        const date = new Date(dateString);
        return date.toLocaleTimeString('vi-VN', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };
    
    // Fetch initial queue data
    useEffect(() => {
        fetchQueue();
        
        // Setup WebSocket connection
        const wsProtocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
        const wsUrl = `${wsProtocol}//${window.location.host}/ws/queue/`;
        
        const newSocket = new WebSocket(wsUrl);
        
        newSocket.onopen = () => {
            console.log('WebSocket connection established');
        };
        
        newSocket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            // Update queue based on received data
            if (data.type === 'queue_update') {
                setQueue(data.queue);
            }
        };
        
        newSocket.onerror = (error) => {
            console.error('WebSocket error:', error);
            setError('Không thể kết nối tới hệ thống cập nhật thời gian thực');
        };
        
        newSocket.onclose = () => {
            console.log('WebSocket connection closed');
        };
        
        setSocket(newSocket);
        
        // Cleanup on unmount
        return () => {
            if (newSocket) {
                newSocket.close();
            }
        };
    }, []);
    
    const fetchQueue = async () => {
        try {
            setLoading(true);
            setError(null);
            const response = await axios.get('/api/queue/');
            setQueue(response.data);
        } catch (err) {
            console.error('Error fetching queue:', err);
            setError('Không thể tải dữ liệu hàng đợi');
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <Box sx={{ 
            m: 2, 
            p: 2, 
            display: 'flex', 
            flexDirection: 'column',
            height: '100vh',
            bgcolor: 'background.default'
        }}>
            <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
                Danh sách hàng đợi
            </Typography>
            
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}
            
            <Alert severity="info" sx={{ mb: 2 }}>
                Danh sách hàng đợi được cập nhật tự động theo thời gian thực. 
                Vui lòng chờ khi đến lượt của bạn.
            </Alert>
            
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>STT</TableCell>
                            <TableCell>Tên khách hàng</TableCell>
                            <TableCell>Thời gian vào hàng đợi</TableCell>
                            <TableCell>Trạng thái</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {loading ? (
                            <TableRow>
                                <TableCell colSpan={4} align="center">
                                    <CircularProgress size={24} sx={{ mr: 1 }} />
                                    Đang tải...
                                </TableCell>
                            </TableRow>
                        ) : queue.length === 0 ? (
                            <TableRow>
                                <TableCell colSpan={4} align="center">
                                    Không có khách hàng nào trong hàng đợi
                                </TableCell>
                            </TableRow>
                        ) : (
                            queue.map((item, index) => (
                                <TableRow 
                                    key={item.id}
                                    sx={{
                                        bgcolor: index === 0 ? 'rgba(144, 238, 144, 0.2)' : 'inherit'
                                    }}
                                >
                                    <TableCell>{index + 1}</TableCell>
                                    <TableCell>
                                        {item.name}
                                        {index === 0 && (
                                            <Chip 
                                                label="Đang phục vụ" 
                                                color="primary" 
                                                size="small" 
                                                sx={{ ml: 1 }}
                                            />
                                        )}
                                    </TableCell>
                                    <TableCell>{formatTime(item.joined_at)}</TableCell>
                                    <TableCell>
                                        <Chip 
                                            label={
                                                item.status === 'waiting' ? 'Đang chờ' : 
                                                item.status === 'served' ? 'Đã phục vụ' : 'Đã hủy'
                                            }
                                            color={
                                                item.status === 'waiting' ? 'warning' : 
                                                item.status === 'served' ? 'success' : 'error'
                                            }
                                            size="small"
                                        />
                                    </TableCell>
                                </TableRow>
                            ))
                        )}
                    </TableBody>
                </Table>
            </TableContainer>
            
            <Box sx={{ mt: 'auto', textAlign: 'center', p: 2 }}>
                <Typography variant="body2" color="text.secondary">
                    © {new Date().getFullYear()} Gamine Restaurant
                </Typography>
            </Box>
        </Box>
    );
};

export default QueueView;
